mapper.update:
        UPDATE	  ddt_user
        SET		  user_name = #{userName}
        		, password = md5(#{password})
		        , wx_number = #{wxName}
		        , mobile = #{mobile}
		WHERE	id = #{id}

        UPDATE	  ddt_wx_user
        SET		  user_name = #{userName}
		        , wx_number = #{wxName}
		        , mobile = #{mobile}
		        , password = md5(#{password})
		WHERE	id = #{id}

mapper.select:
SELECT	  book_id		bookId
		, user_id		userId
FROM	ddt_roll_book_user
WHERE	book_id = #{rollBookId}
		AND user_id = #{userId}

mapper.delete:
DELETE 	FROM ddt_roll_book_user
WHERE	book_id = #{bookId}
		AND user_id = #{userId}
